mapper.select:
select o.total_price total_price,m.name m_name,TO_CHAR(o.order_date,'YYYY-MM-DD') order_date, o.order_no order_no,p.name p_name,o.payment payment
from GURADA_ORDER o ,MEMBER m ,PRODUCT p
where o.member_no = m.member_no and o.product_no = p.product_id and o.payment =#{paymentMethod} and TO_CHAR(ORDER_DATE, 'YYYY-MM-DD') =#{orderDate}

select o.total_price total_price,TO_CHAR(o.order_date,'YYYY-MM-DD') order_date, o.order_no order_no,p.name p_name,p.PRICE price
from GURADA_ORDER o ,MEMBER m,PRODUCT p
where o.member_no = m.member_no and o.product_no = p.product_id and m.name=#{userId}
