mapper.insert:
Insert into 	product	(product_id, name, p_size, price, 
						 detail, gender, category_id,
						 receipt_date, quantity
						 )
 				values (seq_productid_no.nextval, #{name}, #{psize},
 				 		#{price}, #{pdetail}, #{gender},
 				 		#{categoryid}, sysdate, #{quantity}
 				 		)

mapper.select:
select name, price, p_size 
from product
where gender = #{gender} and category_id= #{category_id} and name=#{name}
