mapper.insert:
Insert into 	product	(product_id, name, p_size, price, 
						 detail, gender, category_id,
						 receipt_date, quantity , pfname ,pfsize
						 )
 				values (seq_productid_no.nextval, #{name}, #{pSize},
 				 		#{price}, #{pDetail}, #{gender},
 				 		#{categoryId}, sysdate, #{quantity} , #{pfname} , #{pfsize}
 				 		)

mapper.select:
select name, price, p_size 
from product
where gender = #{gender} and category_id= #{categoryId} and name=#{name}

SELECT * 
	FROM product
		WHERE	category_id=#{categoryId} AND gender=#{gender}
